# FUNCTIONS
###########

def countNchars(array, n)
  counted = 0
  array.each { |handle|
    if handle.length == (n+1)
      counted += 1
    end
  }
  return counted
end

def countCapitalized(array)
  counted = 0
  array.each { |handle|
    if handle[1].capitalize! == nil 
      counted += 1
    end
  }
  return counted
end

def sortByLength(array)  
  return array.sort_by { |handle| handle.length }
end

def displayByLength(array)
  arraySorted = sortByLength(array)
  min = arraySorted[0].length
  max = arraySorted[-1].length
  for i in min..max do
    puts puts "Voici les pseudos contenant #{i-1} caractères :"
    arraySorted.each { |handle|
      if (handle.length == i)
        print " #{handle}"
      end
    }
    puts puts puts
  end
end

